import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Scheduler = () => {
    const [mentors, setMentors] = useState([]);
    const [studentName, setStudentName] = useState('');
    const [areaOfInterest, setAreaOfInterest] = useState('');
    const [duration, setDuration] = useState(30);
    const [sessionTime, setSessionTime] = useState('');

    useEffect(() => {
        axios.get('/api/mentors').then(response => setMentors(response.data));
    }, []);

    const handleSchedule = () => {
        const studentData = { name: studentName, area_of_interest: areaOfInterest };
        axios.post('/api/students', studentData).then(response => {
            const sessionData = {
                student_id: response.data.id,
                mentor_id: mentors[0].id, // Assign the first mentor for simplicity
                duration,
                session_time: sessionTime
            };
            return axios.post('/api/sessions', sessionData);
        }).then(() => {
            alert('Session scheduled successfully!');
        }).catch(err => {
            console.error(err);
            alert('Error scheduling session.');
        });
    };

    return (
        <div>
            <input type="text" placeholder="Student Name" onChange={e => setStudentName(e.target.value)} />
            <input type="text" placeholder="Area of Interest" onChange={e => setAreaOfInterest(e.target.value)} />
            <select onChange={e => setDuration(e.target.value)}>
                <option value="30">30 mins</option>
                <option value="45">45 mins</option>
                <option value="60">60 mins</option>
            </select>
            <input type="datetime-local" onChange={e => setSessionTime(e.target.value)} />
            <button onClick={handleSchedule}>Schedule Session</button>
        </div>
    );
};

export default Scheduler;
